
from vigenere_crypto import frequencyCrack, bruteforceCrack
import timeit, functools

cipher_text = ''

frequencyCrack(cipher_text)

frequency_timer = timeit.Timer(functools.partial(frequencyCrack, cipher_text))
print(frequency_timer.timeit(10))

bruteforce_timer = timeit.Timer(functools.partial(bruteforceCrack, cipher_text))
print(bruteforce_timer.timeit(10))

